import axios from 'axios'

const Info = ({countries}) => {
  if (countries.length > 1) return null
  
  const country = countries[0][0]
  
  console.log(country)
  
  axios
    .get(`https://studies.cs.helsinki.fi/restcountries/api/name/${country}`)
    .then(response => {
      
      return (
        <div>
          <h2>{country}</h2>
        </div>
      )
    })
}

export default Info
