import axios from 'axios'

const Countries = ({ countrySearched, countries, setCountries }) => {
  axios
    .get('https://studies.cs.helsinki.fi/restcountries/api/all')
    .then(response => {
      const countriesNames = response.data.map(country => {
        return Object.values(country.name).slice(0, 2)
      })
      
      setCountries(countriesNames.filter(countryNames => {
        countryNames.some(countryName => {
          countryName.includes(countrySearched)
        })
      }))
      console.log(countries)
    })
  
  return (
    <ul>
      {countries.map(country => <li>{country[0]}</li>)}
    </ul>
  )
}


export default Countries
